tinymce.PluginManager.add('googleChart', function(editor, url) {
  function showDialog() {
        var url = "http://github.com/bekilewis/googleChartsTinyMCE";
        var frame = editor.windowManager.open({
                title: 'Graph Tool',
                url: 'js/tinymce/plugins/googleChart/dialog.asp',
                width: 900,
                height: 700,
                buttons: [{
                    text: 'Close without saving',
                    onclick: 'close'
                }],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    //editor.insertContent('Title: ' + e.data.title);
                }
            });
		window.my_namespace_activeEditor = editor;
        window.my_namespace_activeFrame = frame;
	}

    // Add a button that opens a window
    editor.addButton('googleChart', {
        image: "js/tinymce/plugins/googleChart/img/logo.gif",
        icon: true,
        onclick: showDialog
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('googleChart', {
        image: "js/tinymce/plugins/googleChart/img/logo.gif",
        icon: true,
        text: 'Graph Tool',
        context: 'tools',
        onclick: showDialog
    });
});
